mapper.select:
SELECT board_id, title, content, writer, created_at, updated_at
FROM board
WHERE board_id = #{boardId}

SELECT board_id, title, content, writer, created_at, updated_at FROM board
title LIKE CONCAT('%', #{searchTerm}, '%') OR writer LIKE CONCAT('%', #{searchTerm}, '%')
ORDER BY board_id DESC LIMIT #{size} OFFSET #{offset}
